function updateResults(){type=$("#movie-type").val(),genre=$("#movie-genre").val(),newValue=$("#new-releases").is(":checked"),$(".movie").each(function(){$(this).show(),0!=type&&$(this).data("type")!=type&&$(this).hide(),0!=genre&&$(this).data("genre")!=genre&&$(this).hide(),newValue&&"true"!=$(this).data("new")&&$(this).hide()})}$("#movie-type, #movie-genre, #new-releases").on("change",function(e){updateResults()});